import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Projects from './components/Projects';
import AllProjects from './components/AllProjects';
import CoffeeChat from './components/CoffeeChat';
import Footer from './components/Footer';

function App() {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [allProjectsRef, setAllProjectsRef] = React.createRef();

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    if (allProjectsRef.current) {
      const allProjectsTop = allProjectsRef.current.offsetTop;
      const allProjectsHeight = allProjectsRef.current.offsetHeight;
      const windowScrollTop = window.scrollY;

      if (windowScrollTop >= allProjectsTop && windowScrollTop < allProjectsTop + allProjectsHeight) {
        document.body.style.backgroundColor = 'black';
      } else {
        document.body.style.backgroundColor = '';
      }
    }
  }, [scrollPosition, allProjectsRef]);

  return (
    <div className="App">
      <Header />
      <Main />
      <Projects />
      <div ref={allProjectsRef}>
        <AllProjects />
      </div>
      <CoffeeChat />
      <Footer />
    </div>
  );
}

export default App;
